import React, { useState, useEffect } from 'react';
import './App.css';
import HomeScreen from './components/HomeScreen';
import AddMoneyScreen from './components/AddMoneyScreen';

const SCREENS = {
  HOME: 'home',
  ADD_MONEY: 'add_money',
  SELECT_RECIPIENT: 'select_recipient',
  ENTER_AMOUNT: 'enter_amount',
  CONFIRM_TRANSFER: 'confirm_transfer',
  RECEIPT: 'receipt'
};

function App() {
  const [currentScreen, setCurrentScreen] = useState(SCREENS.HOME);
  const [balance, setBalance] = useState(1106900.63);
  const [selectedRecipient, setSelectedRecipient] = useState(null);
  const [transferAmount, setTransferAmount] = useState(0);
  const [lastTransfer, setLastTransfer] = useState(null);

  // Cargar datos del localStorage al iniciar
  useEffect(() => {
    const savedBalance = localStorage.getItem('balance');
    if (savedBalance) {
      setBalance(parseFloat(savedBalance));
    }
  }, []);

  // Guardar balance en localStorage cuando cambie
  useEffect(() => {
    localStorage.setItem('balance', balance.toString());
  }, [balance]);

  const navigateToScreen = (screen) => {
    setCurrentScreen(screen);
  };

  const handleAddMoney = (amount) => {
    setBalance(prevBalance => prevBalance + amount);
    setCurrentScreen(SCREENS.HOME);
  };

  const handleScanQR = () => {
    // Simular apertura de c치mara
    alert('Abriendo c치mara para escanear QR...');
  };

  const renderScreen = () => {
    switch (currentScreen) {
      case SCREENS.HOME:
        return (
          <HomeScreen 
            balance={balance}
            onAddMoney={() => navigateToScreen(SCREENS.ADD_MONEY)}
            onTransfer={() => alert('Transferir - En desarrollo')}
            onScanQR={handleScanQR}
          />
        );
      case SCREENS.ADD_MONEY:
        return (
          <AddMoneyScreen 
            onConfirm={handleAddMoney}
            onBack={() => navigateToScreen(SCREENS.HOME)}
          />
        );
      default:
        return (
          <HomeScreen 
            balance={balance}
            onAddMoney={() => navigateToScreen(SCREENS.ADD_MONEY)}
            onTransfer={() => alert('Transferir - En desarrollo')}
            onScanQR={handleScanQR}
          />
        );
    }
  };

  return (
    <div className="container">
      {renderScreen()}
    </div>
  );
}

export default App;
